context:
type Chatroom {
    name: String!
    messages: [Message!]!
}

type Message {
    id: ID!

    createdBy: String!
    createdAt: Time!
}

type Query {
    room(name:String!): Chatroom
}

type Mutation {
    post(text: String!, username: String!, roomName: String!): Message!
}

type Subscription {
    messageAdded(roomName: String!): Message!
}

scalar Time

directive @user(username: String!) on SUBSCRIPTION
